import React, { Component } from "react";
import axios from "axios";
import "./App.css";
import Search from "./components/search.js";
import Video from "./components/video";
import List from "./components/list";
import Details from "./components/details";

class mainApp extends Component {
  state = {
    githubData: {},
    id: 0
  };

  componentDidMount() {
    let request = `https://api.github.com/users/sqoor`;

    axios
      .get(request)
      .then(response => {
        this.setState({ githubData: response.data });
      })
      .catch(error => {
        console.log(error);
      });
  }

  clickHandler = i => {
    this.setState({ id: i });
  };

  searchRequest = s => {
    let request = `https://api.github.com/users/${s}`;
    axios
      .get(request)
      .then(response => {
        this.setState({ githubData: response.data, id: 0 });
      })
      .catch(error => {
        console.log(error);
      });
  };

  render() {
    return (
      <div className="container">
        <h1>github API</h1>
        <Search className="top" searchRequest={this.searchRequest} />
