import React, { Component } from "react";
import axios from "axios";
import "./App.css";
import Search from "./components/search.js";
import Video from "./components/video";
import List from "./components/list";
import Details from "./components/details";

class mainApp extends Component {
  state = {
    gitData: {},
    id: 0
  };

  componentDidMount() {
    let request = `https://api.github.com/users/sqoor`;

    axios
      .get(request)
      .then(response => {
        this.setState({ gitData: response.data });
      })
      .catch(error => {
        console.log(error);
      });
  }

  clickHandler = i => {
    this.setState({ id: i });
  };

  searchRequest = s => {
    let request = `https://api.github.com/users/${s}`;
    axios
      .get(request)
      .then(response => {
        this.setState({ gitData: response.data, id: 0 });
      })
      .catch(error => {
        console.log(error);
      });
  };

  render() {
    return (
      <div className="container">
        <h1>github API</h1>
        <Search className="top" searchRequest={this.searchRequest} />

        <div className="bottom">
          {this.state.gitData.items && (
            <div className="videoAndDetail">
              <Video vid={this.state.gitData.items[this.state.id].id.videoId} />
              <Details
                info={this.state.gitData.items[this.state.id].snippet.description}
                title={this.state.gitData.items[this.state.id].snippet.title}
              />
            </div>
          )}

          {this.state.gitData.items && (
            <List className="list" items={this.state.gitData.items} clickHandler={this.clickHandler} />
          )}
        </div>
      </div>
    );
  }
}

export default mainApp;
